SELECT "/I" AS [Transaction Type], [>Resources].SageID AS [Customer ID], "" AS [Invoice #], Format([TransactionDate],"MM/DD/YY") AS [Invoice Date], Format([DebitAmount],"#.00") AS [Invoice Amount], Left(Left([>Jobs.ID],9) & ":" & [SourceTransaction],20) AS Description, Format([TransactionDate],"mm/dd/yy") AS [Due Date], "" AS [Discount Amount], "" AS [Discount Date], "" AS [Sales Rep #], "205" AS [Sales Account #], Format(0,"#.00") AS [Freight Amount], Format(0,"#.00") AS [1st Sales Tax Amount], "" AS [1st Sales Tax ID], Format(0,"#.00") AS [2nd Sales Tax Amount], "" AS [2nd Sales Tax ID], Format(0,"#.00") AS [3rd Sales Tax Amount], "" AS [3rd Sales Tax ID]
FROM ([>General Ledger Query] LEFT JOIN [>Jobs] ON [>General Ledger Query].Job=[>Jobs].Key) LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key
WHERE ((([>General Ledger Query].AccountNumber)=121) And (([>General Ledger Query].TransactionDate)>=forms!generalledger.fromdate And ([>General Ledger Query].TransactionDate)<=forms!generalledger!todate) And (([>General Ledger Query].SourceJournal)="Invoice") And (([>General Ledger Query].DebitAmount)>0))
ORDER BY Format([TransactionDate],"MM/DD/YY");