SELECT "/P" AS [Transaction Type], [>Resources].SageID AS [Customer ID], "" AS [Check #], Format([TransactionDate],"MM/DD/YY") AS [Payment Date], Format([CreditAmount],"#.00") AS [Payment Amount], Left([>Jobs.ID],12) AS Description, "" AS [Cash Account #], "" AS [Credit Card Company Name], "" AS [Credit Card Holder Name], "" AS [Credit Card Holder Address 1], "" AS [Credit Card Holder Address2], "" AS [Credit Card Holder City], "" AS [Credit Card Holder State], "" AS [Credit Card Holder Zip], "" AS [Credit Card Holder E-mail], "" AS [Credit Card Number], "" AS [Credit Card Expiration Date], "" AS [Credit Card Approval Code], "" AS [Open Credit #], "" AS [Open Credit Description]
FROM [Receivables Owed Query] LEFT JOIN (([>General Ledger Query] LEFT JOIN [>Jobs] ON [>General Ledger Query].Job=[>Jobs].Key) LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key) ON [Receivables Owed Query].Job=[>General Ledger Query].Job
WHERE ((([>General Ledger Query].AccountNumber)=121) AND (([>General Ledger Query].SourceJournal)="Receipt") AND (([sumofdebitamount]\1<>[sumofcreditamount]\1)=True) AND (([>General Ledger Query].TransactionDate)>=#1/1/2000#))
ORDER BY [>Resources].SageID, Format([TransactionDate],"MM/DD/YY");