SELECT "/A" AS [Transaction Type], [>Resources].SageID AS [Customer ID], First(Left([>Resources.ID],40)) AS [Customer Name], First(Left(ResourceAddress1([>Resources.Key]),40)) AS [Address Line 1], First(Left(ResourceAddress2([>Resources.Key]),40)) AS [Address Line 2], First(Left(ResourceTown([>Resources.Key]),40)) AS City, [>Resources].State, [>Resources].Zip, First(ResourcePhone1([>Resources].Key)) AS [Finance Contact Telephone Number], "" AS [Finance Contact Name], First(ResourceEmail1([>Resources].Key)) AS [Finance Contact E-mail], First(ResourceComment1([>Resources].Key)) AS [Comment Line 1], First(ResourceComment2([>Resources].Key)) AS [Comment Line 2], "" AS [Reseller Sales Tax ID], Min(Format([>General Ledger Query_1.TransactionDate],"mm/dd/yy")) AS [Customer Since], "" AS [Finance Contact Fax Number]
FROM ([>General Ledger Query] LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key) LEFT JOIN [>General Ledger Query] AS [>General Ledger Query_1] ON [>General Ledger Query].SourceOperative=[>General Ledger Query_1].SourceOperative
WHERE ((([>General Ledger Query].AccountNumber)=121) And (([>General Ledger Query].TransactionDate)>=forms!generalledger.fromdate And ([>General Ledger Query].TransactionDate)<=forms!generalledger!todate))
GROUP BY [>Resources].SageID, [>Resources].State, [>Resources].Zip
ORDER BY First(Left([>Resources.ID],40));