SELECT "/P" AS [Transaction Type], [>Resources].SageID AS [Customer ID], "" AS [Check #], Format([TransactionDate],"MM/DD/YY") AS [Payment Date], Format([DebitAmount],"#.00") AS [Payment Amount], Left([>Jobs.ID],12) AS Description, "" AS [Cash Account #], "" AS [Credit Card Company Name], "" AS [Credit Card Holder Name], "" AS [Credit Card Holder Address 1], "" AS [Credit Card Holder Address2], "" AS [Credit Card Holder City], "" AS [Credit Card Holder State], "" AS [Credit Card Holder Zip], "" AS [Credit Card Holder E-mail], "" AS [Credit Card Number], "" AS [Credit Card Expiration Date], "" AS [Credit Card Approval Code], "" AS [Open Credit #], "" AS [Open Credit Description]
FROM ([>General Ledger Query] LEFT JOIN [>Jobs] ON [>General Ledger Query].Job=[>Jobs].Key) LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key
WHERE ((([>General Ledger Query].AccountNumber)=101) And (([>General Ledger Query].TransactionDate)>=forms!generalledger.fromdate And ([>General Ledger Query].TransactionDate)<=forms!generalledger!todate) And (([>General Ledger Query].SourceJournal)="Receipt") And ((nnez([>Jobs.Key])=0)=True))
ORDER BY Format([TransactionDate],"MM/DD/YY");