SELECT "/I" AS [Transaction Type], [>Resources].SageID AS [Vendor ID], Left([SourceTransaction],20) AS [Invoice #], Format([TransactionDate],"MM/DD/YY") AS [Invoice Date], Format([CreditAmount],"#.00") AS [Invoice Amount], Left([SourceTransaction],20) AS Reference, "" AS [Purchase Order Number], Format([TransactionDate]+30,"mm/dd/yy") AS [Terms or Due Date], "" AS [Discount Amount], "" AS [Discount Date], Format([AccountNo]*10,"#0.00") AS [1st Distribution Account], Format([CreditAmount],"#.00") AS [1st Distribution Amount]
FROM ([>General Ledger Query] LEFT JOIN [Purchase Journal] ON [>General Ledger Query].SourceKey=[Purchase Journal].Key) LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key
WHERE ((([>General Ledger Query].AccountNumber)=201) And (([>General Ledger Query].TransactionDate)>=forms!generalledger.fromdate And ([>General Ledger Query].TransactionDate)<=forms!generalledger!todate) And (([>General Ledger Query].SourceJournal)="Purchase"))
ORDER BY Format([TransactionDate],"MM/DD/YY");