SELECT "/I" AS [Transaction Type], [>Resources].SageID AS [Vendor ID], Left([SourceTransaction],20) AS [Invoice #], Format([TransactionDate],"MM/DD/YY") AS [Invoice Date], Format([CreditAmount],"#.00") AS [Invoice Amount], Left([SourceTransaction],20) AS Reference, "" AS [Purchase Order Number], Format([TransactionDate]+30,"mm/dd/yy") AS [Terms or Due Date], "" AS [Discount Amount], "" AS [Discount Date], Format([Purchase Journal.AccountNo]*10,"#0.00") AS [1st Distribution Account], Format([CreditAmount],"#.00") AS [1st Distribution Amount], [>General Ledger Query].AccountNumber
FROM ([>General Ledger Query] RIGHT JOIN [Purchase Journal] ON [>General Ledger Query].SourceKey=[Purchase Journal].Key) LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key
WHERE ((([>General Ledger Query].AccountNumber)=201) And (([Purchase Journal.PayablesAccount]=201 And [Purchase Journal.InvoiceDate]<=forms!GeneralLedger!ToDate And [CheckDate]>forms!GeneralLedger!ToDate Or [PayablesAccount]=201 And [InvoiceDate]<=forms!GeneralLedger!ToDate And [Paid]=False)=True))
ORDER BY Format([TransactionDate],"MM/DD/YY");