SELECT First("/A") AS [Transaction Type], [>Resources].SageID AS [Vendor ID], First(Left([>Resources].ID,40)) AS [Vendor Name], First(Left(ResourceAddress1([>Resources.Key]),40)) AS [Address Line 1], First(Left(ResourceAddress2([>Resources.Key]),40)) AS [Address Line 2], First(Left(ResourceTown([>Resources.Key]),40)) AS City, First([>Resources].State) AS FirstOfState, First([>Resources].Zip) AS FirstOfZip, First(ResourcePhone1([>Resources].Key)) AS [Finance Contact Telephone Number], First("") AS [Finance Contact Name], First("") AS [Federal ID Number], First(ResourceComment1([>Resources].Key)) AS [Comment Line 1], First(ResourceComment2([>Resources].Key)) AS [Comment Line 2], First("") AS [Finance Contact Fax Number], First(ResourceEmail1([>Resources].Key)) AS [Finance Contact EMail]
FROM [>General Ledger Query] LEFT JOIN [>Resources] ON [>General Ledger Query].SourceOperative=[>Resources].Key
WHERE ((([>General Ledger Query].AccountNumber)=201) And (([>General Ledger Query].TransactionDate)>=forms!generalledger.fromdate And ([>General Ledger Query].TransactionDate)<=forms!generalledger!todate))
GROUP BY [>Resources].SageID;