SELECT DISTINCTROW [General Ledger Balance Sheet].Index, [General Ledger Balance Sheet].Heading, [General Ledger Balance Sheet].SubHeading, [General Ledger Balance Sheet].SubSubHeading, [General Ledger Balance Sheet].[Index Income], [General Ledger Balance Sheet].[Index Expense], [General Ledger Balance Sheet Account Order].FY9, [General Ledger Balance Sheet Account Order].Balance, [General Ledger Balance Sheet Account Order].Account, nnez([SumOfCreditAmount]) AS TheCredit, nnez([SumOfDebitAmount]) AS TheDebit, nnez([sumofcreditamount])-nnez([sumofdebitamount]) AS TheNet
FROM [General Ledger Balance Sheet] LEFT JOIN [General Ledger Balance Sheet Account Order] ON [General Ledger Balance Sheet].Index=[General Ledger Balance Sheet Account Order].BalanceSheetItemSort
GROUP BY [General Ledger Balance Sheet].Index, [General Ledger Balance Sheet].Heading, [General Ledger Balance Sheet].SubHeading, [General Ledger Balance Sheet].SubSubHeading, [General Ledger Balance Sheet].[Index Income], [General Ledger Balance Sheet].[Index Expense], [General Ledger Balance Sheet Account Order].FY9, [General Ledger Balance Sheet Account Order].Balance, [General Ledger Balance Sheet Account Order].Account, nnez([SumOfCreditAmount]), nnez([SumOfDebitAmount]);