SELECT [MIS Personnel].Employee, [>Jobs].ID, [>Jobs].ProductionPhase, [Job Cabinet and Counter Sales].LastOfTheDate, [Job Agreement Sales].SumOfSaleAmount, [Job Cabinet and Counter Sales].SumOfSaleAmount, [Job Stone Sales].SumOfSaleAmount, [Job Install Sales].SumOfSaleAmount, [Job Other Sales].SumOfSaleAmount, [Job Project Hours].SumOfHours, [Job Project Hours].SumOfCost, [Job Cabinet Hours].SumOfHours, [Job Cabinet Hours].SumOfCost, [Job Install Hours].SumOfHours, [Job Install Hours].SumOfCost, [>Jobs].Info1
FROM (((((((([>Jobs] LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key) LEFT JOIN [Job Agreement Sales] ON [>Jobs].Key=[Job Agreement Sales].Key) LEFT JOIN [Job Cabinet and Counter Sales] ON [>Jobs].Key=[Job Cabinet and Counter Sales].Key) LEFT JOIN [Job Cabinet Hours] ON [>Jobs].Key=[Job Cabinet Hours].Key) LEFT JOIN [Job Install Hours] ON [>Jobs].Key=[Job Install Hours].Key) LEFT JOIN [Job Other Sales] ON [>Jobs].Key=[Job Other Sales].Key) LEFT JOIN [Job Project Hours] ON [>Jobs].Key=[Job Project Hours].Key) LEFT JOIN [Job Stone Sales] ON [>Jobs].Key=[Job Stone Sales].Key) LEFT JOIN [Job Install Sales] ON [>Jobs].Key=[Job Install Sales].Key
WHERE ((([>Jobs].Estimate)=False) AND (([MIS Personnel].Text2)="Partner"))
ORDER BY [MIS Personnel].Employee, [>Jobs].ID;