SELECT DISTINCTROW [>Jobs].ID, [>Jobs].Info1, Last([Sales Journal].TheDate) AS LastOfTheDate1, Min([MIS Personnel].Employee) AS MinOfEmployee, [>Jobs].Completed, [Job Agreement Sales].SumOfSaleAmount, [Job Cabinet and Counter Sales].SumOfSaleAmount, [Job Install Sales].SumOfSaleAmount, [Job Project Hours].TotalCost, [Job Cabinet Hours].TotalCost, [Job Install Hours].TotalCost, IIf(NNEZ([Job Cabinet and Counter Sales].SumofSaleAmount)+NNEZ([Job Install Sales].SumofSaleAmount)-NNEZ([Job Project Hours].TotalCost)-NNEZ([Info1])-NNEZ([Job Cabinet Hours].TotalCost)-NNEZ([Job Install Hours].TotalCost)>0,-1,0) AS Profitable, NNEZ([Job Cabinet and Counter Sales].SumofSaleAmount)+NNEZ([Job Install Sales].SumofSaleAmount) AS TotalJobSales, IIf(NNEZ([>Jobs].Completed)=0,[LastofTheDate1],[>Jobs].Completed) AS CompletionDate
FROM ((((((((([Sales Journal] RIGHT JOIN [>Jobs] ON [Sales Journal].Job=[>Jobs].Key) LEFT JOIN [Job Agreement Sales] ON [>Jobs].Key=[Job Agreement Sales].Key) LEFT JOIN [Job Cabinet and Counter Sales] ON [>Jobs].Key=[Job Cabinet and Counter Sales].Key) LEFT JOIN [Job Cabinet Hours] ON [>Jobs].Key=[Job Cabinet Hours].Key) LEFT JOIN [Job Install Hours] ON [>Jobs].Key=[Job Install Hours].Key) LEFT JOIN [Job Install Sales] ON [>Jobs].Key=[Job Install Sales].Key) LEFT JOIN [Job Other Sales] ON [>Jobs].Key=[Job Other Sales].Key) LEFT JOIN [Job Project Hours] ON [>Jobs].Key=[Job Project Hours].Key) LEFT JOIN [Job Stone Sales] ON [>Jobs].Key=[Job Stone Sales].Key) LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key
WHERE ((([>Jobs].Audit)=True) AND (([Sales Journal].TheDate) Is Not Null) AND (([>Jobs].Estimate)=False))
GROUP BY [>Jobs].ID, [>Jobs].Info1, [>Jobs].Completed, [Job Agreement Sales].SumOfSaleAmount, [Job Cabinet and Counter Sales].SumOfSaleAmount, [Job Install Sales].SumOfSaleAmount, [Job Project Hours].TotalCost, [Job Cabinet Hours].TotalCost, [Job Install Hours].TotalCost
ORDER BY [>Jobs].ID;