SELECT DISTINCTROW [>Payroll Query].*, [MIS Personnel].WitholdingAccount, [vacationbalance]<>0 Or [AccruedVacation]<>0 Or [retroactivevacation]<>0 Or [vacation]<>0 AS Expr1, DateDiff("d",Format([CheckDate],"mm/dd"),Format([mis personnel].Employmentdate,"mm/dd")) AS Anniversary, [Payroll Hours Vacation].Hours AS V, [Payroll Hours Owed Vacation].Hours AS OV
FROM (([>Payroll Query] LEFT JOIN [MIS Personnel] ON [>Payroll Query].ADPFile=[MIS Personnel].ADPFile) LEFT JOIN [Payroll Hours Vacation] ON ([>Payroll Query].CheckDate=[Payroll Hours Vacation].PayCheck) AND ([>Payroll Query].ADPFile=[Payroll Hours Vacation].ADPFile)) LEFT JOIN [Payroll Hours Owed Vacation] ON ([>Payroll Query].CheckDate=[Payroll Hours Owed Vacation].PayCheck) AND ([>Payroll Query].ADPFile=[Payroll Hours Owed Vacation].ADPFile)
WHERE ((([vacationbalance]<>0 Or [AccruedVacation]<>0 Or [retroactivevacation]<>0 Or [vacation]<>0)=True) And (([>Payroll Query].Void)=False) And (([>Payroll Query].ADPFile)>5) And (([>Payroll Query].ADPNetAccount)=IIf(Forms!Payroll!SelectADPNet,Forms!Payroll!ADPNetAccount,[>Payroll Query].ADPNetAccount)));