SELECT NoDashes([MIS Personnel].SocialSecurity) AS A, Mid([MIS Personnel].ADPName,InStr([MIS Personnel].ADPName,',')+1,Len([MIS Personnel].ADPName)) AS B, Left([MIS Personnel].ADPName,InStr([MIS Personnel].ADPName,',')-1) AS C, Format([MIS Personnel].BIRTHDATE,'mm/dd/yyyy') AS D, Format([MIS Personnel].EmploymentDATE,'mm/dd/yyyy') AS E, Format([MIS Personnel].TerminationDate,'mmddyyyy') AS F, [>401K Hours YTD Query].SumOfHours AS G, [MIS Personnel].Sex AS H, [>401K ADP YTD Query].SumOfGross AS I, "795412" AS J, "" AS K, "" AS L, "A" AS M, [SumofField3]-[SumofCommission] AS N, "" AS O, "" AS P, "" AS Q, "" AS R, "" AS S, "" AS T, "" AS U, "" AS V, "" AS W, "" AS X, "" AS Y, "" AS Z, "" AS AA, "" AS AB, "" AS AC, ResourceStreet1([>Resources].Key) AS AD, "" AS AE, ResourceTown([>Resources].Key) AS AF, [>Resources].State AS AG, "11" AS AH, Left([>Resources].Zip,5) AS AI, "" AS AJ, IIf([SumofCommission]<>0,"1","") AS AK, [>401K ADP Period Query].SumOfCommission AS AL, "" AS AM, "" AS AN, "" AS AO, "" AS AP, "" AS AQ, "" AS AR, "" AS [AS], "" AS [AT], "" AS AU, "" AS AV, "" AS AW, "" AS AX, "" AS AY, "" AS AZ, "" AS BA, "" AS BB, "" AS BC, "" AS BD, [>401K ADP Period Query].CheckDateFormatted AS BE
FROM ((([>401K ADP Period Query] LEFT JOIN [>401K Hours YTD Query] ON [>401K ADP Period Query].ADPFile=[>401K Hours YTD Query].FirstOfADPFile) LEFT JOIN [>401K ADP YTD Query] ON [>401K ADP Period Query].ADPFile=[>401K ADP YTD Query].ADPFile) LEFT JOIN [MIS Personnel] ON [>401K ADP Period Query].ADPFile=[MIS Personnel].ADPFile) LEFT JOIN [>Resources] ON [MIS Personnel].Resource=[>Resources].Key
