SELECT DISTINCTROW [>Balance Sheet All Accounts Report Query].Order, [>Balance Sheet All Accounts Report Query].Heading, [>Balance Sheet All Accounts Report Query].Sort AS Expr1, [>Balance Sheet All Accounts Report Query].SubHeading, [>Balance Sheet All Accounts Report Query].Account, [>Balance Sheet All Accounts Report Query].BeginningBalance, [>Balance Sheet Accounts Report GL Query].Net AS Expr2, IIf([order]<100,Left([order],1),Mid([Order],2,1)) AS SubOrder, IIf([Order]<100,"Asset","Liability") AS AssetorLiability
FROM [>Balance Sheet All Accounts Report Query] LEFT JOIN [>Balance Sheet Accounts Report GL Query] ON [>Balance Sheet All Accounts Report Query].Account=[>Balance Sheet Accounts Report GL Query].AccountNumber
GROUP BY [>Balance Sheet All Accounts Report Query].Order, [>Balance Sheet All Accounts Report Query].Heading, [>Balance Sheet All Accounts Report Query].Sort, [>Balance Sheet All Accounts Report Query].SubHeading, [>Balance Sheet All Accounts Report Query].Account, [>Balance Sheet All Accounts Report Query].BeginningBalance, [>Balance Sheet Accounts Report GL Query].Net, IIf([order]<100,Left([order],1),Mid([Order],2,1)), IIf([Order]<100,"Asset","Liability")
ORDER BY [>Balance Sheet All Accounts Report Query].Order, [>Balance Sheet All Accounts Report Query].Sort, [>Balance Sheet All Accounts Report Query].SubHeading DESC;