SELECT DISTINCTROW [MIS Hours].Date, [MIS Hours].Employee, [MIS Personnel].ADPNetAccount, [MIS Hours].Activity, [MIS Hours].General, [MIS Hours].[Job ID], [MIS Hours].Charge, [MIS Hours].Hours, [MIS Hours].ADPFile, [MIS Hours].Key, [MIS Hours].Cost, [MIS Hours].Rate, [>Jobs].ID AS Job, IIf(IsNull([Activity]),[General],[Activity]) AS AorG, IIf(Not IsNull([General]),0,IIf(Forms!Hours.ActualCost,IIf([ADPNetAccount]>=700,([MIS Hours].Rate+variable("Design OH"))*[Hours],([MIS Hours].Rate+variable("S&I OH"))*[Hours]),IIf([ADPNetAccount]>=700,variable("office hrly cost estimate")*[Hours],variable("shop hrly cost estimate")*[Hours]))) AS CostPlus, IIf(Forms!Hours!EstimatedCost=True,IIf([ADPNetAccount]>=700,variable("office hrly cost estimate"),variable("shop hrly cost estimate")),[MIS Hours].Rate) AS HourlyRate, IIf(Forms!Hours!EstimatedCost=True,IIf([ADPNetAccount]>=700,variable("office hrly cost estimate")*[Hours],variable("shop hrly cost estimate")*[Hours]),[MIS Hours].Cost) AS HourlyCost
FROM ([MIS Hours] LEFT JOIN [>Jobs] ON [MIS Hours].[Job ID]=[>Jobs].Key) LEFT JOIN [MIS Personnel] ON [MIS Hours].Employee=[MIS Personnel].Employee
WHERE ((([MIS Hours].Hours)<>0));