SELECT DISTINCTROW Left([order],1) AS Category, [>Balance Sheet All Accounts Report Query].Order, [>Balance Sheet All Accounts Report Query].Heading, [>Balance Sheet All Accounts Report Query].SubHeading, [>Balance Sheet All Accounts Report Query].Account, [>Balance Sheet All Accounts Report Query].Account AS Sort, [>Balance Sheet All Accounts Report Query].BeginningBalance, [>Balance Sheet All Accounts Report Query].PreviousBalance, [credit]-[debit] AS Net, [previouscredit]-[previousdebit] AS PreviousNet, IIf([order]<100,Left([order],1),Mid([Order],2,1)) AS SubOrder, IIf([Order]<30,"Asset","Liability and Stockholder Equity") AS AssetorLiability
FROM [>Balance Sheet All Accounts Report Query] LEFT JOIN [>Balance Sheet Accounts Report All GL Query] ON [>Balance Sheet All Accounts Report Query].Account=[>Balance Sheet Accounts Report All GL Query].Number
GROUP BY Left([order],1), [>Balance Sheet All Accounts Report Query].Order, [>Balance Sheet All Accounts Report Query].Heading, [>Balance Sheet All Accounts Report Query].SubHeading, [>Balance Sheet All Accounts Report Query].Account, [>Balance Sheet All Accounts Report Query].Account, [>Balance Sheet All Accounts Report Query].BeginningBalance, [>Balance Sheet All Accounts Report Query].PreviousBalance, [credit]-[debit], [previouscredit]-[previousdebit], IIf([order]<100,Left([order],1),Mid([Order],2,1)), IIf([Order]<30,"Asset","Liability and Stockholder Equity")
ORDER BY Left([order],1), [>Balance Sheet All Accounts Report Query].Order, [>Balance Sheet All Accounts Report Query].SubHeading DESC;