SELECT DISTINCTROW IIf(Not IsNull([terminationdate]) And [employmentdate]>=Forms!Payroll!FromDate,"Hired and Terminated",IIf(Not IsNull([terminationdate]),"Terminated","Hired")) AS HiredorFired, [MIS Personnel].ADPName, [MIS Personnel].EmploymentDate, [MIS Personnel].Text2, [MIS Personnel].TerminationDate, [MIS Personnel].Closed, IIf([TerminationDate] Is Null,[Rate],IIf([employmentdate]>=forms!Payroll!fromdate And [terminationdate]<forms!Payroll!todate,0,[rate]*-1)) AS TheRate, [MIS Personnel].ADPFile, Format([TerminationDate],"mmmm") AS [Month], Format([EmploymentDate],"dd") AS [Day], IIf(IsNull([terminationdate]),Format([EmploymentDate],"yyyy"),Format([TerminationDate],"yyyy")) AS [Year], IIf(Not IsNull([terminationdate]),Format(DateDiff("d",[EmploymentDate],[TerminationDate])/365,"#.0"),Format(DateDiff("d",[EmploymentDate],forms!Payroll!Todate)/365,"#.0")) AS Years, Format([EmploymentDate],"mmmm") AS TheMonth, IIf(Year([employmentDate])<Year(Now()),Format(DateDiff("d",[CompensationChange],Now())/365,"#.#"),Null) AS ReviewYears, IIf([ReviewYears]>=1,[ReviewYears] & " yr.","") AS ReviewDue, Format(Now(),"m") AS ThisMonth, IIf([employmentdate]>=Forms!Payroll!FromDate And [employmentdate]<=Forms!Payroll!Todate Or Not IsNull([terminationdate]) And [Terminationdate]>=Forms!Payroll!FromDate And [Terminationdate]<=Forms!Payroll!Todate,True,False) AS Expr1, 1 AS TheCount
FROM [MIS Personnel]
WHERE ((([MIS Personnel].ADPFile)>999) And ((IIf([employmentdate]>=Forms!Payroll!FromDate And [employmentdate]<=Forms!Payroll!Todate Or Not IsNull([terminationdate]) And [Terminationdate]>=Forms!Payroll!FromDate And [Terminationdate]<=Forms!Payroll!Todate,True,False))=True))
ORDER BY [MIS Personnel].ADPName;