SELECT DISTINCTROW GLSalesQuery.[>Jobs].ID, GLSalesQuery.MaxOfDue, GLSalesQuery.SumOfInvoiceAmount, GLReceiptsQuery.SumofAmount, GLSalesQuery.[>Resources].ID, [MIS Personnel].Employee, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, IIf(Date()-[MaxofDue]>30 And NNEZ([SumofInvoiceAmount])-NNEZ([SumofAmount])>0,NNEZ([SumofInvoiceAmount])-NNEZ([SumofAmount]),0) AS Over30, IIf(Date()-[MaxofDue]<=30 And NNEZ([SumofInvoiceAmount])-NNEZ([SumofAmount])>0,NNEZ([SumofInvoiceAmount])-NNEZ([SumofAmount]),0) AS [Current], IIf(NNEZ([SumofInvoiceAmount])-NNEZ([SumofAmount])<0,NNEZ([SumofInvoiceAmount])-NNEZ([SumofAmount]),0) AS CreditBalance
FROM ((GLSalesQuery LEFT JOIN GLReceiptsQuery ON GLSalesQuery.Key=GLReceiptsQuery.Key) LEFT JOIN [>Jobs] ON GLSalesQuery.Key=[>Jobs].Key) LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key;