SELECT DISTINCTROW [MIS Hours].Employee, [MIS Hours].Date, [MIS Hours].Archive AS Expr1, [MIS Hours].EntryDate AS Expr2, [MIS Hours].[Job ID], [MIS Hours].Activity, [MIS Hours].Charge, [MIS Hours].DesignNote, [MIS Hours].Journal, [MIS Hours].ADPFile, [MIS Hours].General, [MIS Hours].GeneralHour, [MIS Hours].JobHour, [MIS Hours].Hours, [MIS Hours].Rate, [MIS Hours].MondayNote, [MIS Hours].TuesdayNote, [MIS Hours].WednesdayNote, [MIS Hours].ThursdayNote, [MIS Hours].FridayNote, [MIS Hours].SaturdayNote, [MIS Hours].SundayNote, [MIS Hours].Cost, [MIS Hours].Monday, [MIS Hours].Tuesday, [MIS Hours].Wednesday, [MIS Hours].Thursday, [MIS Hours].Friday, [MIS Hours].Saturday, [MIS Hours].[Job ID], [MIS Hours].Activity, [MIS Hours].Sunday, [MIS Hours].Key
FROM [MIS Hours]
WHERE ((([MIS Hours].Employee)=Forms!HoursEntry!EmpName) And (([MIS Hours].Date)=Forms!HoursEntry!Week) And (([MIS Hours].[Job ID]) Is Null Or ([MIS Hours].[Job ID])=0) And (([MIS Hours].Journal)="General" Or ([MIS Hours].Journal) Is Null) And (([MIS Hours].JobHour)=False) And ((IIf(nnez([Job ID])=0 And nnen([activity])="",True,False))=True Or (IIf(nnez([Job ID])=0 And nnen([activity])="",True,False))=False));