SELECT DISTINCTROW [MIS Hours].Employee, [MIS Hours].Date, [MIS Hours].[Job ID], [MIS Hours].Activity, [MIS Hours].DesignNote, [>Jobs].Archive, [MIS Hours].EntryDate AS Expr1, [MIS Hours].Journal, [MIS Hours].GeneralHour, [MIS Hours].JobHour, [MIS Hours].Charge, [MIS Hours].Hours, [MIS Hours].SaturdayNote, [MIS Hours].SundayNote, [MIS Hours].MondayNote, [MIS Hours].TuesdayNote, [MIS Hours].WednesdayNote, [MIS Hours].ThursdayNote, [MIS Hours].FridayNote, [MIS Hours].Rate, [MIS Hours].Cost, [MIS Hours].Monday, [MIS Hours].Tuesday, [MIS Hours].Wednesday, [MIS Hours].Thursday, [MIS Hours].Friday, [MIS Hours].Saturday, [MIS Hours].Sunday, [MIS Hours].Key, [MIS Hours].General, [>Jobs].DesignJob, [MIS Hours].ADPFile
FROM [MIS Hours] LEFT JOIN [>Jobs] ON [MIS Hours].[Job ID]=[>Jobs].Key
WHERE ((([MIS Hours].Employee)=Forms!HoursEntry!EmpName) And (([MIS Hours].Date)=Forms!HoursEntry!Week) And (([MIS Hours].[Job ID])<>0) And (([MIS Hours].Journal)="Job" Or ([MIS Hours].Journal) Is Null) And (([MIS Hours].GeneralHour)=False) And ((IIf(nnen([General])="",True,False))=True Or (IIf(nnen([General])="",True,False))=False))
ORDER BY [MIS Hours].Activity;