SELECT [Sales Journal].Key, [>Jobs].ID, IIf([Sales Journal]!Category="Cabinet","Cabinetry",[Sales Journal]!Category) AS TheCategory, IIf(NNEN([Sales Journal]!PrivateNote)="",[TheCategory],[Sales Journal]!PrivateNote) & IIf(NNEN([InvoiceNote_1])<>""," and " & [InvoiceNote_1],"") AS InvoiceNote, IIf(NNEN([Sales Journal_1]!PrivateNote)="",[Sales Journal_1]!Category,[Sales Journal_1]!PrivateNote) AS InvoiceNote_1, [Sales Journal].SaleAmount+NNEZ([Sales Journal_1].SaleAmount) AS Sale_Amount, [Sales Journal_1].SaleAmount, [Sales Journal].InvoiceNumber, [Sales Journal].Job, NNEZ([Sales Journal].LinkSale) AS Expr1
FROM [>Jobs] LEFT JOIN ([Sales Journal] LEFT JOIN [Sales Journal] AS [Sales Journal_1] ON [Sales Journal].Key=[Sales Journal_1].LinkSale) ON [>Jobs].Key=[Sales Journal].Job
WHERE (((IIf(Not forms!Sales!Family And [Sales Journal].Job=forms!Sales!Jobname,True,IIf(forms!Sales!Family And [Parent]=Forms!Sales!JobName Or forms!Sales!Family And [Sales Journal].Job=Forms!Sales!JobName,True,False)))=True) And ((NNEZ([Sales Journal].LinkSale))=0) And (([Sales Journal].Journal)="Sale") And ((InStr([Sales Journal]!Category,"Additional")=0)=True))
ORDER BY [Sales Journal].InvoiceNumber;