SELECT [>Product List].Component, [>Product List].ComponentText, [>Product List].PremiumCost, [>Product List].Entry, [>Product List].OfficeHours, [>Product List].ShopHours, [>Product List].FinishHours, [>Product List].AssemblyHours, [>Product List].InstallHours, [>Product List].TotalCost, [>Product List].SpecialMaterials, [>Product List].Inventory, [>Product List].ShopCost, [>Product List].OfficeCost, IIf(IsNull([officehours]) And IsNull([shophours]) And IsNull([finishhours]) And IsNull([assemblyhours]) And IsNull([Installhours]) And IsNull([inventory]) And IsNull([specialmaterials]),False,True) AS Expr1
FROM [>Product List]
WHERE ((([>Product List].ComponentText)="Legacy") AND (([>Product List].PremiumCost) Is Not Null) AND (([>Product List].Entry)>#1/1/2016#) AND ((IIf(IsNull([officehours]) And IsNull([shophours]) And IsNull([finishhours]) And IsNull([assemblyhours]) And IsNull([Installhours]) And IsNull([inventory]) And IsNull([specialmaterials]),False,True))=True))
ORDER BY [>Product List].Entry DESC;