SELECT DISTINCTROW [>Jobs].ID, [>Jobs].ProductionPhase, [MIS Personnel].Employee, [*InvoicesQuery2].MaxOfDue, [*InvoicesQuery2].Invoices, [*PaymentsQuery2_1].Payments, [*PaymentsQuery2_1].MaxOfCheckDate, Date()-[maxofcheckdate] AS PayDays, NNEZ([invoices])-NNEZ([payments])+NNEZ([DebitAmount]) AS Balance, IIf([Balance]>0,[Balance],0) AS DrBal, IIf([balance]<0,[balance],0) AS CrBal, Date()-[MaxofDue] AS Days, [*RefundQuery].DebitAmount, [>Jobs].Estimate, IIf([days]<31,[drbal],0) AS Under, IIf([days]>30,[drbal],0) AS Over, IIf([days]>30,2,1) AS Sort
FROM ((([>Jobs] LEFT JOIN [*InvoicesQuery2] ON [>Jobs].ID=[*InvoicesQuery2].ID) LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key) LEFT JOIN [*RefundQuery] ON [>Jobs].ID=[*RefundQuery].ID) LEFT JOIN [*PaymentsQuery2] AS [*PaymentsQuery2_1] ON [>Jobs].ID=[*PaymentsQuery2_1].ID
WHERE (((NNEZ([invoices])-NNEZ([payments])+NNEZ([DebitAmount]))<>0) AND (([>Jobs].Estimate)=False) AND (([>Jobs].Closed)=False) AND (([>Jobs].Owner)=False))
ORDER BY Date()-[MaxofDue] DESC;