SELECT DISTINCTROW [>Sales Customer Total Query].Key, [>Sales Customer Total Query].SumOfSumOfSaleAmount, [>Resources].ID AS ResourceID, [>Jobs].ID AS JobID, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [Job Agreement Sales NIC Stone].SumOfSaleAmount, [>Jobs].ProductionPhase, [Job Agreement Sales NIC Stone].LastOfTheDate, [Job Project Hours].TotalCost, [Job Cabinet Hours].TotalCost
FROM ([>Resources] RIGHT JOIN [>Sales Customer Total Query] ON [>Resources].Key=[>Sales Customer Total Query].Key) LEFT JOIN ((([>Jobs] LEFT JOIN [Job Project Hours] ON [>Jobs].Key=[Job Project Hours].Key) LEFT JOIN [Job Cabinet Hours] ON [>Jobs].Key=[Job Cabinet Hours].Key) LEFT JOIN [Job Agreement Sales NIC Stone] ON [>Jobs].Key=[Job Agreement Sales NIC Stone].Key) ON [>Sales Customer Total Query].Key=[>Jobs].Customer
WHERE ((([>Jobs].Estimate)=False))
GROUP BY [>Sales Customer Total Query].Key, [>Sales Customer Total Query].SumOfSumOfSaleAmount, [>Resources].ID, [>Jobs].ID, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [Job Agreement Sales NIC Stone].SumOfSaleAmount, [>Jobs].ProductionPhase, [Job Agreement Sales NIC Stone].LastOfTheDate, [Job Project Hours].TotalCost, [Job Cabinet Hours].TotalCost
HAVING ((([Job Agreement Sales NIC Stone].SumOfSaleAmount)<>0));