SELECT DISTINCTROW First([MIS Personnel].Employee) AS FirstOfEmployee, [>Jobs].Info2, First([>Jobs].ID) AS FirstOfID1, [>Sales Proposals Open Query].MaxOfDue, Sum([Sales Journal].SaleAmount) AS SumOfSaleAmount, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Jobs].Key, First([>Resources].ID) AS FirstOfID, First([>Jobs].Category) AS FirstOfCategory, [>Resources_1].ID, [>Resources_2].ID, [>Resources_3].ID, [>Resources_4].ID
FROM [>Sales Proposals Open Query] LEFT JOIN ([Sales Journal] LEFT JOIN (((((([>Jobs] LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key) LEFT JOIN [>Resources] ON [>Jobs].Customer=[>Resources].Key) LEFT JOIN [>Resources] AS [>Resources_1] ON [>Jobs].Architect=[>Resources_1].Key) LEFT JOIN [>Resources] AS [>Resources_2] ON [>Jobs].Contractor=[>Resources_2].Key) LEFT JOIN [>Resources] AS [>Resources_3] ON [>Jobs].Contact1=[>Resources_3].Key) LEFT JOIN [>Resources] AS [>Resources_4] ON [>Jobs].DesignedBy=[>Resources_4].Key) ON [Sales Journal].Job=[>Jobs].Key) ON [>Sales Proposals Open Query].Key=[Sales Journal].Job
GROUP BY [>Jobs].Info2, [>Sales Proposals Open Query].MaxOfDue, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Jobs].Key, [>Resources_1].ID, [>Resources_2].ID, [>Resources_3].ID, [>Resources_4].ID
ORDER BY First([MIS Personnel].Employee);