SELECT DISTINCTROW ProjectSchedule.TaskEnd, ProjectSchedule.Task, ProjectSchedule.PercentComplete, [>Jobs].ID, [MIS Personnel].Employee, ProjectSchedule.Job, [>Jobs].ProductionPhase, [>Sales Total Cabinets].SumOfSaleAmount, ProjectSchedule.EntryDate, [>Jobs].ProductionPhase, [MIS Personnel].Employee, [MIS Personnel_1].Employee, [MIS Personnel_2].Employee, [MIS Personnel].Text1, [MIS Personnel_1].Text1, [MIS Personnel_2].Text1, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Jobs].EstimatedBy
FROM (((([>Jobs] RIGHT JOIN ProjectSchedule ON [>Jobs].Key=ProjectSchedule.Job) LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key) LEFT JOIN [MIS Personnel] AS [MIS Personnel_1] ON [>Jobs].SpecifiedBy=[MIS Personnel_1].Key) LEFT JOIN [MIS Personnel] AS [MIS Personnel_2] ON [>Jobs].EstimatedBy=[MIS Personnel_2].Key) LEFT JOIN [>Sales Total Cabinets] ON ProjectSchedule.Job=[>Sales Total Cabinets].Job
WHERE ((([>Jobs].ProductionPhase)<>"Completed" And ([>Jobs].ProductionPhase)<>"Archive") AND ((ProjectSchedule.TaskDuration)=0) AND (([>Jobs].Estimate)=False) AND (([>Jobs].Closed)=False) AND ((InStr([task],"Deliver"))<>0))
ORDER BY ProjectSchedule.TaskEnd;