SELECT DISTINCTROW First([MIS Personnel].Employee) AS FirstOfEmployee, [>Jobs].ID, [>Jobs].Info2, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, Max([Sales Journal].Due) AS MaxOfDue, [MIS Personnel].Text2, Sum([Sales Journal].SaleAmount) AS SumOfSaleAmount, First([>Resources].ID) AS FirstOfID, First([>Jobs].Category) AS FirstOfCategory, [>Resources_1].ID, [>Resources_2].ID, [>Resources_3].ID, [>Resources_4].ID, [>Jobs].Closed
FROM ((((([Sales Journal] RIGHT JOIN ([>Jobs] LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key) ON [Sales Journal].Job=[>Jobs].Key) LEFT JOIN [>Resources] ON [>Jobs].Customer=[>Resources].Key) LEFT JOIN [>Resources] AS [>Resources_1] ON [>Jobs].Architect=[>Resources_1].Key) LEFT JOIN [>Resources] AS [>Resources_2] ON [>Jobs].Contractor=[>Resources_2].Key) LEFT JOIN [>Resources] AS [>Resources_3] ON [>Jobs].Contact1=[>Resources_3].Key) LEFT JOIN [>Resources] AS [>Resources_4] ON [>Jobs].DesignedBy=[>Resources_4].Key
GROUP BY [>Jobs].ID, [>Jobs].Info2, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [MIS Personnel].Text2, [>Resources_1].ID, [>Resources_2].ID, [>Resources_3].ID, [>Resources_4].ID, [>Jobs].Closed, [>Jobs].Estimate
HAVING ((([MIS Personnel].Text2)="Partner") AND (([>Jobs].Estimate)=True))
ORDER BY First([MIS Personnel].Employee), [>Jobs].ID;