SELECT DISTINCTROW [>Sales Interior Designer Total Query].Key, [>Resources].ID AS ResourceID, [>Sales Interior Designer Total Query].SumOfSumOfSaleAmount, [>Jobs].ID AS JobID, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [Job Agreement Sales NIC Stone].SumOfSaleAmount, [>Jobs].ProductionPhase, [Job Agreement Sales NIC Stone].LastOfTheDate, [Job Project Hours].TotalCost, [Job Cabinet Hours].TotalCost
FROM ([>Resources] RIGHT JOIN [>Sales Interior Designer Total Query] ON [>Resources].Key=[>Sales Interior Designer Total Query].Key) LEFT JOIN ((([>Jobs] LEFT JOIN [Job Project Hours] ON [>Jobs].Key=[Job Project Hours].Key) LEFT JOIN [Job Cabinet Hours] ON [>Jobs].Key=[Job Cabinet Hours].Key) LEFT JOIN [Job Agreement Sales NIC Stone] ON [>Jobs].Key=[Job Agreement Sales NIC Stone].Key) ON [>Sales Interior Designer Total Query].Key=[>Jobs].DesignedBy
WHERE ((([>Jobs].Estimate)=False))
GROUP BY [>Sales Interior Designer Total Query].Key, [>Resources].ID, [>Sales Interior Designer Total Query].SumOfSumOfSaleAmount, [>Jobs].ID, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [Job Agreement Sales NIC Stone].SumOfSaleAmount, [>Jobs].ProductionPhase, [Job Agreement Sales NIC Stone].LastOfTheDate, [Job Project Hours].TotalCost, [Job Cabinet Hours].TotalCost
HAVING ((([Job Agreement Sales NIC Stone].SumOfSaleAmount)<>0));