SELECT DISTINCTROW ProjectSchedule.TaskEnd, [>Receivables Query].Days, ProjectSchedule.TaskBegin, ProjectSchedule.Task, ProjectSchedule.TaskDuration, ProjectSchedule.PercentComplete, [>Job Receipts Query].SumOfAmount, [>Job Hours Cost Query].SumOfHours, [>Job Hours Cost Query].SumOfCost, [>Jobs].ID, ProjectSchedule.Job, [>Jobs].ProductionPhase, [>Sales Total Cabinets].SumOfSaleAmount, ProjectSchedule.EntryDate, [>Jobs].ProductionPhase, [MIS Personnel_1].Employee, [MIS Personnel_2].Employee, [MIS Personnel_3].Text1, [MIS Personnel_1].Text1, [MIS Personnel_2].Text1, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Jobs].EstimatedBy, NNE1([CountOfTaskSummary]) AS Expr1
FROM (((((((([>Jobs] RIGHT JOIN ProjectSchedule ON [>Jobs].Key=ProjectSchedule.Job) LEFT JOIN [MIS Personnel] AS [MIS Personnel_1] ON [>Jobs].SpecifiedBy=[MIS Personnel_1].Key) LEFT JOIN [MIS Personnel] AS [MIS Personnel_2] ON [>Jobs].EstimatedBy=[MIS Personnel_2].Key) LEFT JOIN [>Sales Total Cabinets] ON ProjectSchedule.Job=[>Sales Total Cabinets].Job) LEFT JOIN [>Schedule Phase Query] ON [>Jobs].ID=[>Schedule Phase Query].ID) LEFT JOIN [>Job Receipts Query] ON [>Jobs].Key=[>Job Receipts Query].Key) LEFT JOIN [>Job Hours Cost Query] ON [>Jobs].Key=[>Job Hours Cost Query].Key) LEFT JOIN [>Receivables Query] ON [>Jobs].Key=[>Receivables Query].Key) LEFT JOIN [MIS Personnel] AS [MIS Personnel_3] ON [>Jobs].ManagedBy=[MIS Personnel_3].Key
WHERE (((ProjectSchedule.TaskDuration)=0) AND (([>Jobs].ProductionPhase)<>"Completed" And ([>Jobs].ProductionPhase)<>"Archive") AND (([>Jobs].Estimate)=False) AND (([>Jobs].Closed)=False))
ORDER BY ProjectSchedule.TaskEnd;