SELECT DISTINCTROW [>Jobs].ID, [MIS Personnel].Employee, [MIS Personnel_1].Text1, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Sales Total Cabinets].SumOfSaleAmount, Count(ProjectSchedule.Job) AS CountOfJob, [>Jobs].ProductionPhase, Sum([>Job Hours Project Cost Query].SumOfCost) AS SumOfSumOfCost, Sum([>Job Hours Project Cost Query].SumOfHours) AS SumOfSumOfHours
FROM (((([>Jobs] LEFT JOIN [MIS Personnel] ON [>Jobs].ManagedBy=[MIS Personnel].Key) LEFT JOIN ProjectSchedule ON [>Jobs].Key=ProjectSchedule.Job) LEFT JOIN [>Sales Total Cabinets] ON [>Jobs].Key=[>Sales Total Cabinets].Job) LEFT JOIN [MIS Personnel] AS [MIS Personnel_1] ON [>Jobs].SpecifiedBy=[MIS Personnel_1].Key) LEFT JOIN [>Job Hours Project Cost Query] ON [>Jobs].Key=[>Job Hours Project Cost Query].Key
GROUP BY [>Jobs].ID, [MIS Personnel].Employee, [MIS Personnel_1].Text1, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Sales Total Cabinets].SumOfSaleAmount, [>Jobs].ProductionPhase, [>Jobs].Estimate, [>Jobs].Closed
HAVING (((Count(ProjectSchedule.Job))=0) AND (([>Jobs].ProductionPhase)="Preliminary" Or ([>Jobs].ProductionPhase)="Design") AND (([>Jobs].Estimate)=False) AND (([>Jobs].Closed)=False))
ORDER BY [>Jobs].ID, [MIS Personnel].Employee;