SELECT DISTINCTROW WorkFlow.TaskEnd, WorkFlow.Key, [>Receivables Query].Days, WorkFlow.TaskBegin, WorkFlow.Task, WorkFlow.TaskDuration, WorkFlow.PercentComplete, WorkFlow.PhaseAmount, WorkFlow.Engineer, [>Job Receipts Query].SumOfAmount, [>Job Hours Cost Query].SumOfHours, [>Job Hours Cost Query].SumOfCost, WorkFlow.Phase, [>Jobs].ID, WorkFlow.Job, [>Jobs].ProductionPhase, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Sales Total Cabinets].SumOfSaleAmount, [>Jobs].ProductionPhase, [MIS Personnel_1].Employee, [MIS Personnel_2].Employee, [MIS Personnel_3].Text1, [MIS Personnel_1].Text1, [MIS Personnel_2].Text1, [>Jobs].ManagedBy, [>Jobs].SpecifiedBy, [>Jobs].EstimatedBy, NNE1(1) AS Expr1
FROM (WorkFlow LEFT JOIN ((((((([>Jobs] LEFT JOIN [MIS Personnel] AS [MIS Personnel_1] ON [>Jobs].SpecifiedBy=[MIS Personnel_1].Key) LEFT JOIN [MIS Personnel] AS [MIS Personnel_2] ON [>Jobs].EstimatedBy=[MIS Personnel_2].Key) LEFT JOIN [>Schedule Phase Query] ON [>Jobs].ID=[>Schedule Phase Query].ID) LEFT JOIN [>Job Receipts Query] ON [>Jobs].Key=[>Job Receipts Query].Key) LEFT JOIN [>Job Hours Cost Query] ON [>Jobs].Key=[>Job Hours Cost Query].Key) LEFT JOIN [>Receivables Query] ON [>Jobs].Key=[>Receivables Query].Key) LEFT JOIN [MIS Personnel] AS [MIS Personnel_3] ON [>Jobs].ManagedBy=[MIS Personnel_3].Key) ON WorkFlow.Job=[>Jobs].Key) LEFT JOIN [>Sales Total Cabinets] ON WorkFlow.Job=[>Sales Total Cabinets].Job
WHERE ((([>Jobs].ProductionPhase)<>"Completed" And ([>Jobs].ProductionPhase)<>"Archive") AND (([>Jobs].Estimate)=False) AND (([>Jobs].Closed)=False))
ORDER BY WorkFlow.TaskEnd;