SELECT tblPeopleDayAttendance.ClientID, tblPeopleDayAttendance.LastName, tblPeopleDayAttendance.FirstName, tblPeopleDayAttendance.MiddleInitial, tblPeopleDayAttendance.City, tblPeopleDayAttendance.Loc, tblPeopleDayAttendance.Prf, tblPeopleDayAttendance.Sev, tblPeopleDayAttendance.Rate, tblPeopleDayAttendance.Fund, tblPeopleDayAttendance.SpecialFundingCode, tblPeopleDayAttendance.STM, tblPeopleDayAttendance.IsDeceased, tblPeopleDayAttendance.CostCenter, tblPeopleDayAttendance.UnitsMO, tblPeopleDayAttendance.UnitsTU, tblPeopleDayAttendance.UnitsWE, tblPeopleDayAttendance.UnitsTH, tblPeopleDayAttendance.UnitsFR, Month(DateValue([tblPeopleDayAttendance]![WeekEnding])) AS Expr1, Day(DateValue([tblPeopleDayAttendance]![WeekEnding])) AS Expr2, Year(DateValue([tblPeopleDayAttendance]![WeekEnding])) AS Expr3 INTO temptbl0
FROM tblPeopleDayAttendance
WHERE (((tblPeopleDayAttendance.City)=Forms!frmMainMenu!SelectedCity) And ((tblPeopleDayAttendance.Loc)=Forms!frmMainMenu!SelectedProgram) And ((Month(DateValue(tblPeopleDayAttendance!WeekEnding)))=Forms!frmPeopleDayAttendanceInit!CurrentWeekEndingMonth) And ((Day(DateValue(tblPeopleDayAttendance!WeekEnding)))=Forms!frmPeopleDayAttendanceInit!CurrentWeekEndingDay) And ((Year(DateValue(tblPeopleDayAttendance!WeekEnding)))=Forms!frmPeopleDayAttendanceInit!CurrentWeekEndingYear))
ORDER BY tblPeopleDayAttendance.LastName, tblPeopleDayAttendance.FirstName;