INSERT INTO tblExpirations ( Location, RecordType, LastName, FirstName, Supervisor, LastVehicleChecklistCompleted, MostRecentAsleepFireDrill, NextRecentAsleepFireDrill, HouseSafetyPlanExpires, HousePlansReviewedByStaffBefore, DAYStaffTrainedInPrivacyBefore, DAYAllPlansReviewedByStaffBefore, DAYQtrlySafetyChecklistDueBy, MAPChecklistCompleted, HumanRightsOfficer, HROTrainsStaffBefore, HROTrainsIndividualsBefore, FireSafetyOfficer, FSOTrainsStaffBefore, FSOTrainsIndividualsBefore )
SELECT GPName AS Location, "House" AS RecordType, "*" AS LastName, "*" AS FirstName, DLookUp("GPSuperCode","temptbl","GPName='" & tblLocations.GPName & "'") AS Supervisor, LastVehicleChecklistCompleted, MostRecentAsleepFireDrill, NextRecentAsleepFireDrill, HouseSafetyPlanExpires, HousePlansReviewedByStaffBefore, DAYStaffTrainedInPrivacyBefore, DAYAllPlansReviewedByStaffBefore, DAYQtrlySafetyChecklistDueBy, MAPChecklistCompleted, tblLocations.HumanRightsOfficer, tblLocations.HROTrainsStaffBefore, tblLocations.HROTrainsIndividualsBefore, tblLocations.FireSafetyOfficer, tblLocations.FSOTrainsStaffBefore, tblLocations.FSOTrainsIndividualsBefore
FROM tblLocations
WHERE DLookUp("GPSuperCode","temptbl","GPName='" & tblLocations.GPName & "'") Is Not Null AND tblLocations.GPName Is Not Null AND tblLocations.Department <> "Clinical and Support Services";