SELECT temptbl0.ClientID AS Expr1, temptbl0.LastName AS Expr2, temptbl0.FirstName AS Expr3, temptbl0.MiddleInitial AS Expr4, temptbl0.City AS Expr5, temptbl0.Loc AS Expr6, temptbl0.Prf AS Expr7, temptbl0.Sev AS Expr8, temptbl0.Rate AS Expr9, temptbl0.Fund AS Expr10, temptbl0.SpecialFundingCode AS Expr11, temptbl0.IsDeceased AS Expr12, temptbl0.CostCenter AS Expr13, temptbl0.WeekEnding AS Expr14, temptbl0.CodeMO AS Expr15, temptbl0.HrsMO AS Expr16, temptbl0.CodeTU AS Expr17, temptbl0.HrsTU AS Expr18, temptbl0.CodeWE AS Expr19, temptbl0.HrsWE AS Expr20, temptbl0.CodeTH AS Expr21, temptbl0.HrsTH AS Expr22, temptbl0.CodeFR AS Expr23, temptbl0.HrsFR AS Expr24, IIf([CodeMO]="P",24,IIf([CodeMO]="ST",24,IIf([HrsMO]>=3,24,IIf([HrsMO]<3,12,0)))) AS UnitsMO, IIf([CodeTU]="P",24,IIf([CodeTU]="ST",24,IIf([HrsTU]>=3,24,IIf([HrsTU]<3,12,0)))) AS UnitsTU, IIf([CodeWE]="P",24,IIf([CodeWE]="ST",24,IIf([HrsWE]>=3,24,IIf([HrsWE]<3,12,0)))) AS UnitsWE, IIf([CodeTH]="P",24,IIf([CodeTH]="ST",24,IIf([HrsTH]>=3,24,IIf([HrsTH]<3,12,0)))) AS UnitsTH, IIf([CodeFR]="P",24,IIf([CodeFR]="ST",24,IIf([HrsFR]>=3,24,IIf([HrsFR]<3,12,0)))) AS UnitsFR, [UnitsMO]+[UnitsTU]+[UnitsWE]+[UnitsTH]+[UnitsFR] AS WeekUnits, IIf([UnitsMO]=0,1,0)+IIf([UnitsTU]=0,1,0)+IIf([UnitsWE]=0,1,0)+IIf([UnitsTH]=0,1,0)+IIf([UnitsFR]=0,1,0) AS Absences INTO temptbl1
FROM temptbl0;