SELECT DLookUp("ClientID","tblPeopleClientsDemographics","IndexedName=""" & [tblPeopleDayAttendance].[IndexedName] & """") AS ClientID, tblPeopleDayAttendance.LastName, tblPeopleDayAttendance.FirstName, tblPeopleDayAttendance.MiddleInitial, tblPeopleDayAttendance.City, tblPeopleDayAttendance.Loc, tblPeopleDayAttendance.Prf, tblPeopleDayAttendance.Sev, tblPeopleDayAttendance.Rate, tblPeopleDayAttendance.Fund, tblPeopleDayAttendance.SpecialFundingCode, tblPeopleDayAttendance.STM, tblPeopleDayAttendance.IsDeceased, tblPeopleDayAttendance.CostCenter, tblPeopleDayAttendance.WeekEnding, tblPeopleDayAttendance.CodeMO, tblPeopleDayAttendance.HrsMO, tblPeopleDayAttendance.CodeTU, tblPeopleDayAttendance.HrsTU, tblPeopleDayAttendance.CodeWE, tblPeopleDayAttendance.HrsWE, tblPeopleDayAttendance.CodeTH, tblPeopleDayAttendance.HrsTH, tblPeopleDayAttendance.CodeFR, tblPeopleDayAttendance.HrsFR INTO temptbl0
FROM tblPeopleDayAttendance
WHERE (((tblPeopleDayAttendance.City)=Forms!frmMainMenu!SelectedCity) And ((tblPeopleDayAttendance.Loc)=Forms!frmMainMenu!SelectedProgram) And ((DateValue(tblPeopleDayAttendance.WeekEnding))=DateValue(Forms!frmPeopleDayAttendanceInit!CurrentWeekEnding)))
ORDER BY tblPeopleDayAttendance.LastName, tblPeopleDayAttendance.FirstName;